import React, { Component } from 'react';

class Product extends Component {

    constructor(props){
        super(props);
        this.state = {
            nowImgNumber: 0 ,
            isStart: false ,
            playTime: 2000 ,
        }
    }

    getProductType(props) {
        alert(props.productid);
    }

    //  进入该组件时执行
    componentDidMount(props) {
        alert(props);
    }

    //  退出该组件时执行
    componentWillUnmount() {
        alert("componentWillUnmount");
    }

    render() {
        return (
            <div>
                这里是产品界面
                <button onClick="getProductType()">点我</button>
                <div></div>
            </div>
        );
    }
}

export default Product;
