import React, { Component } from 'react';
import axios from 'axios';

import './product.css';

class Product extends Component {

    constructor(props){
        super(props);
        this.state = {
            product : {},
            products : [],
            producttypes : [],
        }
    }
    
    //  进入该组件时执行
    componentDidMount(props) {
        const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。
        //先取产品类型
        _this.getProductType();

        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductInfoById')
        .then(function (response){
            _this.setState({
                product:response.data,
            })
        })
        .catch(function (error) {
            console.log("出错了："+error)
        });

        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductMap')
        .then(function (response){
            _this.setState({
                products:response.data,
            })
            console.log(JSON.stringify(response.data));
        })
        .catch(function (error) {
            console.log("出错了："+error)
        });

        // this.getProductDesc();
    }
    
    getProductType() {
        alert(this.state.products);
        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductType')
        .then(function (response) {
            this.setState({
                producttypes:response.data,
            })
        })
        .catch(function (error) {
            alert("查询产品类型出错"+error);
        });
    }

    getProductDesc() {
        alert("弹框");
    }
    
    //  退出该组件时执行
    componentWillUnmount() {
        
    }

    render() {
        return (
            <div>
                <div className="product-type">产品类型
                    <ul>
                        {
                            this.state.producttypes.map(item => 
                                <li key={item.typeno}>
                                    {item.typename}
                                </li>
                            )
                        }
                    </ul>
                </div>
                <div className="product-list">
                    <ul>
                        {
                            this.state.products.map(item => 
                                <li key={item.productno}>
                                    {item.ProductName}
                                </li>
                            )
                        }
                    </ul>
                </div>
            </div>
        );
    }
}

export default Product;
