import React, { Component } from 'react';
import axios from 'axios';

import './product.css';

class Product extends Component {

    constructor(props){
        super(props);
        this.state = {
            test : "211",
            product : {},
            products : [],
            producttypes : [],
        }
    }
    
    //  进入该组件时执行
    componentDidMount(props) {
        const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。
        _this.getProductType();
        // _this.getProductDesc("hello "," world");
    }
    
    // 获取产品分类
    getProductType =() => {
        const _this = this;
        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductType')
        .then(function (response) {
            _this.setState({
                producttypes:response.data,
            })
        })
        .catch(function (error) {
            alert("查询产品类型出错:"+error);
        });
    }

    // 获取产品列表
    getProductList =() => {
        const _this = this;
        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductMap')
        .then(function (response){
            _this.setState({
                products:response.data,
            })
        })
        .catch(function (error) {
            console.log("出错了："+error)
        });
    }

    // 获取产品详情
    getProductInfo =() => {
        const _this = this;
        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductInfoById')
        .then(function (response){
            _this.setState({
                product:response.data,
            })
        })
        .catch(function (error) {
            console.log("获取产品详情出错："+error)
        });
    }

    getProductDesc =(a,b) => {       
        alert(a+"-"+b);
    }
    
    //  退出该组件时执行
    componentWillUnmount() {
        
    }

    render() {
        return (
            <div>
                <div className="product-type">产品类型
                    <ul>
                        {
                            this.state.producttypes.map(item => 
                                <li key={item.typeno}>
                                    {item.typename}
                                </li>
                            )
                        }
                    </ul>
                </div>
                <div className="product-list">
                    <ul>
                        {
                            this.state.products.map(item => 
                                <li key={item.productno}>
                                    {item.ProductName}
                                </li>
                            )
                        }
                    </ul>
                </div>
            </div>
        );
    }
}

export default Product;
