import React, { Component } from 'react';
import logo from './zyinno.png';
import './home.css';
import axios from 'axios';




//定义一个React组件
class ZYHome extends Component {

  getUserName() {
    alert(123);
    return;
    const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。
    axios.get('https://5b5e71c98e9f160014b88cc9.mockapi.io/api/v1/lists')
    .then(function (response) {
      _this.setState({
        users:response.data,
        isLoaded:true
      });
    })
    .catch(function (error) {
      console.log(error);
      _this.setState({
        isLoaded:false,
        error:error
      })
    })
  }

  render() {  //React组件中必须有一个render函数
    return (
      <div>
        <div className="ZY-top">
          <img src={logo} className="ZY-logo" alt="logo" />
          <ul>
            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="关于智宇">关于智宇</a>
            </li>

            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="智宇未来">智宇未来</a>
            </li>

            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="产品介绍">产品介绍</a>
            </li>
            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="服务对象">服务对象</a>
            </li>
            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="联系我们">联系我们</a>
            </li>

          </ul>
        </div>

        <div className="ZY-div2"> 
          <button onClick={this.getUserName}>请求方法</button>
        </div>
        <div className="ZY-div3">
          <div>深圳市智宇创新科技有限公司</div>
        </div>
      </div>
    );
  }
}

export default ZYHome; //组件定义好之后，导出了在别的地方才能引用
