import React, { Component } from 'react';
import logo from '../../zyinno.png';
import ZYHome from '../../ZYHome';
import './home.css';
import axios from 'axios';

//定义一个React组件
class Top extends Component {

  constructor(props){
    super(props);
    this.state={
      users:[],
      isLoaded:false,
      userName:''
    }
  }

  getUserName() {
    const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。
    axios.get('http://127.0.0.1:8080/getUserName')
    .then(function (response) {
      console.log(response.data);
      _this.setState({
        userName:response.data,
        users:response.data,
        isLoaded:true
      });
    })
    .catch(function (error) {
      console.log(error);
      _this.setState({
        isLoaded:false,
        error:error
      })
    })
  }

  render() {  //React组件中必须有一个render函数
    return (
      <div>
        <div className="ZY-top">
          <img src={logo} className="ZY-logo" alt="logo" />
          <ul>
            <li>
              <a href="main/test.html" target="_self" title="关于智宇">关于智宇</a>
            </li>

            <li>
              <a href="main/top.html" target="_self" title="智宇未来">智宇未来</a>
            </li>

            <li>
              <a href="main/fotter.html" target="_self" title="产品介绍">产品介绍</a>
            </li>
            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="服务对象">服务对象</a>
            </li>
            <li>
              <a href="/about/viewCategoryPage?categoryId=108" target="_self" title="联系我们">联系我们</a>
            </li>
          </ul>
        </div>

        
        </div>
    );}
}

export default Top;
