import React, { Component } from 'react';

var content = document.querySelector('.photoPlay');

class Home extends Component {

    constructor(props){
        this.props = props;
        this.width = this.props.parentNode.offsetWidth; // 获取宽度
        this.length = document.querySelectorAll('.slider-item').length;
        this.index = 0; // 当前页的索引值
        this.animate();        
    }
    
    move () {
        // 当前页的索引值等于最后一页，重置索引值为第一页
        if (this.index === this.length - 1) {
            this.index = 0;
        }
            
        this.index ++;
        this.ele.style.left = - this.index * this.width + 'px';     
    }


    animate() {
        setInterval(this.move.bind(this),2000)
    }


    render() {
        return (
            <div>
                <div className="photoPlay">
                <div class="slider-item">1</div>
                <div class="slider-item">2</div>
                <div class="slider-item">3</div>
                <div class="slider-item">1</div>
                </div>
            </div>
        );
    }
}
export default Home;
