import React, { Component } from 'react';
import './photoplay.css';
class Photoplay extends Component {

    constructor(){
        super();
        this.state = {
            nowImgNumber: 0 ,
            isStart: false ,
            playTime: 2000 ,
        }
    }

    componentDidMount() {
        this.change = setInterval(()=>this.changeNum(),this.state.playTime);
    }

    handleStart() {
        if(this.state.isStart){
            this.change = setInterval(()=>this.changeNum(),this.state.playTime);
        } else {
            clearInterval(this.change);
        }
        this.setState({
            isStart:!this.state.isStart,
        })
    }

    changeNum(){
        if(this.state.nowImgNumber == 5){
            this.setState({
                nowImgNumber:0,
            })
        }else{
            this.setState({
                nowImgNumber:this.state.nowImgNumber+1,
            })
        }
    }

    handleSubmit = (flag)=>{
        clearInterval(this.change);
        if(flag == 1){
            if(this.state.nowImgNumber == 5){
                this.setState({
                    nowImgNumber: 0,
                    isStart: false,
                })
            } else {
                this.setState({
                    nowImgNumber: this.state.nowImgNumber + 1,
                    isStart:false,
                })
            }
        } else {
            if(this.state.nowImgNumber == 0){
                this.setState({
                    nowImgNumber: 5,
                    isStart: false,
                })
            } else {
                this.setState({
                    nowImgNumber: this.state.nowImgNumber - 1,
                    isStart: false,
                })
            }
        }
        this.change = setInterval(()=>this.changeNum(),this.state.playTime);
    }

    handlePoint = (idNum)=>{
        clearInterval(this.change);
        this.setState({
            nowImgNumber: idNum,
            isStart:false,
        })
    }

    handlePointStart=()=>{
        this.change = setInterval(()=>this.changeNum(),this.state.playTime);
    }

    componentWillUnmount(){
        clearInterval(this.change);
    }

    reviseTime = (time)=>{
        clearInterval(this.change);
        this.setState({
            playTime:this.state.playTime+time,
        })
        this.change = setInterval(()=>this.changeNum(),this.state.playTime + time);
    }

    render() {
        return (
            <div className="photoplay">
                这是图片播放的组件
            </div>
        );
    }
}



export default Photoplay;
