import React, { Component } from 'react';
import axios from 'axios';

class Product extends Component {

    constructor(props){
        super(props);
        this.state = {
            product : {},
            products : [],
            producttypes : [],
        }
    }
    
    //  进入该组件时执行
    componentDidMount(props) {
        const _this = this; //先存一下this，以防使用箭头函数this会指向我们不希望它所指向的对象。
        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductInfoById')
        .then(function (response){
            _this.setState({
                product:response.data,
            })
        })
        .catch(function (error) {
            console.log("出错了："+error)
        });

        axios.get('http://127.0.0.1:8081/ZYHOME/product/getProductMap')
        .then(function (response){
            _this.setState({
                products:response.data,
            })
            console.log(JSON.stringify(response.data));
        })
        .catch(function (error) {
            console.log("出错了："+error)
        });
    }
    
    //  退出该组件时执行
    componentWillUnmount() {
        alert("componentWillUnmount");
    }
    
    getProductDesc(props) {
        alert(props);
    }
    
    render() {
        return (
            <div>
                <div>产品
                    <button onClick={this.getProductDesc.bind(this,this.state.product.productno)}>
                        {this.state.product.productno}
                    </button>
                </div>
                <div>
                    <ul>
                        {
                            this.state.products.map(item => 
                                <li key={item.productno}>
                                    {item.ProductName}
                                </li>
                            )
                        }
                    </ul>
                </div>
            </div>
        );
    }
}

export default Product;
